mapper.insert:
INSERT INTO TB_USER_INVENTORY (
    USER_ID,
    PRODUCT_ID,
    QUANTITY
) VALUES (
    #{userId},
    #{productId},
    #{quantity}
)

mapper.select:
SELECT
COUNT(*)
FROM TB_USER
WHERE

    USER_ID = #{value}
    USER_NICKNAME = #{value}
    USER_EMAIL = #{value}
    1 = 0

SELECT USER_ID
FROM TB_USER
WHERE USER_NAME = #{userName} AND USER_EMAIL = #{userEmail}

mapper.update:
UPDATE TB_USER
SET USER_PASSWORD = #{newHashedPassword}
WHERE USER_ID = #{userId}

UPDATE TB_USER_INVENTORY
SET QUANTITY = QUANTITY - 1
WHERE USER_ID = #{userId}
AND PRODUCT_ID = #{productId}
AND QUANTITY > 0
