mapper.update:
UPDATE TB_NOTIFICATION
SET READ_AT = NOW(),
    STATUS = 'read',
    UPDATED_DATE = NOW(),
    UPDATED_BY = #{updatedBy}
WHERE NOTIFICATION_ID = #{id}
    AND USER_ID = #{userId}
    AND STATUS = 'unread'
    AND READ_AT IS NULL

UPDATE TB_NOTIFICATION
SET STATUS = 'deleted',
    UPDATED_DATE = NOW(),
    UPDATED_BY = #{updatedBy}
WHERE USER_ID = #{userId}
    AND NOTIFICATION_ID = #{id}
    AND STATUS != 'deleted'

UPDATE TB_NOTIFICATION
SET STATUS = 'deleted',
    UPDATED_DATE = NOW(),
    UPDATED_BY = #{updatedBy}
WHERE USER_ID = #{userId}
    AND STATUS = 'read'
